changeSet.sql:
DO $$
DECLARE
    new_user_id INT;
BEGIN
    INSERT INTO admin_user (username, password) VALUES ('${admin_superuser}', '${admin_superuser_password}')
    RETURNING id INTO new_user_id;

    INSERT INTO admin_user_role (role, admin_user_id) VALUES ('XROAD_SECURITY_OFFICER', new_user_id);
    INSERT INTO admin_user_role (role, admin_user_id) VALUES ('XROAD_REGISTRATION_OFFICER', new_user_id);
    INSERT INTO admin_user_role (role, admin_user_id) VALUES ('XROAD_SERVICE_ADMINISTRATOR', new_user_id);
    INSERT INTO admin_user_role (role, admin_user_id) VALUES ('XROAD_SYSTEM_ADMINISTRATOR', new_user_id);
    INSERT INTO admin_user_role (role, admin_user_id) VALUES ('XROAD_SECURITYSERVER_OBSERVER', new_user_id);
END
$$;

grant usage on schema "${db_schema}" to "${db_user}";
grant select,update,insert,delete on all tables in schema "${db_schema}" to "${db_user}";
grant select,usage on all sequences in schema "${db_schema}" to "${db_user}";
grant execute on all functions in schema "${db_schema}" to "${db_user}";
revoke insert,update,delete on databasechangelog, databasechangeloglock from "${db_user}";
revoke update,delete on history from "${db_user}";
